# Subroutine returns the name of the Error File Registry (EFR) 
# given the processing type.
#
#------------------------------------------------------------------------------------------------

sub get_error_file_registry_filename {

    #
    # Get input.
    #

    my $i_processing_type   = uc(shift); 

    # Determine which EFR to use depends on the processing type.

    my $r_error_file_registry_filename = "";
 
    if ($i_processing_type eq "QUICKLOOK") {
        $r_error_file_registry_filename = $ENV{QUICKLOOK_FILE_ERROR_REGISTRY_FILENAME};
    } elsif ($i_processing_type eq "REFINED") {
        $r_error_file_registry_filename = $ENV{REFINED_FILE_ERROR_REGISTRY_FILENAME};
    } else {
        print "get_error_file_registry_filename:ERROR, Do not recognize processing type [$i_processing_type]\n";
        print "get_error_file_registry_filename:Returning empty string for file name.\n";
    }

    # Close up shop

    return($r_error_file_registry_filename);
}
